import {Book} from "./Book";
import Header from "../Header";
import {useEffect, useState} from "react";
import axios from "axios";

export function BookList({books = []}) {

  const [book, setBooks] = useState([
    {
      id: '0',
      bookName: '콜롬비아 커피1',
      bookType: '커피빈',
      bookStatus: '북 스테이터스',
      createdAt : '2000-08-01-00-00-00'
    }

  ]);

  // 리액트가 준비가 다 되면 호출되는 훅 함수
  useEffect(() => {
    axios.get("http://localhost:8080/api/v1/books")
    .then(v => setBooks(v.data))

  }, []);

  return (
      <div className="container mx-auto">
        <div>
          <Header headername="책 목록"/>
        </div>
        <div>
          <table className="min-w-full border-collapse block md:table">
            <thead className="block md:table-header-group">
            <tr className="border border-grey-500 md:border-none block md:table-row absolute -top-full md:top-auto -left-full md:left-auto  md:relative ">
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">책 Id</th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">책 이름
              </th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">책 유형
              </th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">책 대여 상태</th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">책 등록일 </th>

            </tr>
            </thead>

            <tbody className="block md:table-row-group">
            {
              // eslint-disable-next-line array-callback-return
              books.map(book => {
                return (<tr className="bg-gray-300 border border-grey-500 md:border-none block md:table-row">
                  <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{book.id}</td>
                  <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{book.bookName}</td>
                  <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{book.bookType}</td>
                  <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{book.bookStatus}</td>
                  <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{book.createdAt}</td>
                </tr>
                )
              })
            }

            <tr className="bg-gray-300 border border-grey-500 md:border-none block md:table-row">
              <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span
                    className="inline-block w-1/3 md:hidden font-bold">Name</span>Jamal
                Rios
              </td>
              <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">
            <span
                className="inline-block w-1/3 md:hidden font-bold">User Name</span>jrios1
              </td>
              <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">
                <span className="inline-block w-1/3 md:hidden font-bold">Email Address</span>jrios@icloud.com
              </td>
              <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">
            <span
                className="inline-block w-1/3 md:hidden font-bold">Mobile</span>582-3X2-6233
              </td>
              <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">
            <span
                className="inline-block w-1/3 md:hidden font-bold">Actions</span>
                <button
                    className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 border border-blue-500 rounded">Edit
                </button>
                <button
                    className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 border border-red-500 rounded">Delete
                </button>
              </td>
            </tr>




              {/*<td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">*/}


              {/*  <span*/}
              {/*  className="inline-block w-1/3 md:hidden font-bold">Actions</span>*/}
              {/*  <button*/}
              {/*      className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-1 px-2 border border-blue-500 rounded">Edit*/}
              {/*  </button>*/}
              {/*  <button*/}
              {/*      className="bg-red-500 hover:bg-red-700 text-white font-bold py-1 px-2 border border-red-500 rounded">Delete*/}
              {/*  </button>*/}
              {/*</td>*/}
            {/*</tr>*/}


            </tbody>
          </table>
        </div>
      </div>
  )
}

/**
 *  <div>
 *         <h1> 북 리스트 </h1>
 *         <div>
 *           {
 *             books ? books.map(v =>
 *                 <Book {...v}/>
 *             ) : <h1> 데이터가 없습니다 </h1>
 *
 *           }
 *
 *         </div>
 *       </div>
