import {useState} from "react";
import {Link} from "react-router-dom";
import axios from "axios";

export function BookAdd() {

  // const [book, setBook]= {
  //   bookName: "",
  //   bookType: ""
  // }

  // const addBook = () => {
  //   // axios.post("https://localhost:8080/api/v1/books", {
  //   //   email: order.email,
  //   //   address: order.address,
  //   //   postcode: order.postcode,
  //   //   orderItems: items.map(v => ({
  //   //     productId: v.productId,
  //   //     category: v.category,
  //   //     price: v.price,
  //   //     quantity: v.count
  //   //
  //   //   }))
  //   // }).then(v => alert("주문이 정상적으로 접수되었습니다."), e => {
  //   //   alert("서버 장애");
  //   //   console.error(e);
  //   // });
  //   console.log(book)
  // }

  // const handleSubmit = () => {
  //   console.log("handle!")
  // }

  const [bookInfo, setBookInfo] = useState({
    bookName: '',
    bookType: ''
  })

  // const {bookName, bookType} = bookInfo;

  const onChange = (e) => {
    const {name, value} = e.target;

    const nextBookInfo = {
      ...bookInfo,
      [name]: value
    }

    setBookInfo(nextBookInfo);
  }

  function validateBookInfo(bookInfo) {
    if (bookInfo.bookName === '' || bookInfo.bookType === '') {
      alert("입력값을 확인해주세요!");
      return false;
    }

    return true;
  }

  const onclickBookAdd = () => {
    if (validateBookInfo(bookInfo)) {
      axios
      .post("http://localhost:8080/api/v1/books", JSON.stringify(bookInfo), {
        headers: {
          "Content-Type": `application/json`,
        },
      }).then(function (response) {
        console.log("responseStatus : " + response.status);
        console.log("console.data : " + response.data);
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
        alert(error);
      });

    }

  }

  const optionValue = ["COMPUTER", "SCIENCE", "ECONOMY", "COMIC", "SPORTS"]

  return (
      <div className="m-4 ">

        <form>
          <div className=" flex flex-row">
            <label htmlFor="inputBookName"
                   className="leading-9">책 이름 : </label>
            <div className="mx-auto w-9/12">
              <input type="text" id="small-input" name="bookName"
                     onChange={onChange}
                     className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500"/>
            </div>
          </div>
          <div className="my-8 flex flex-row">
            <label htmlFor="bookType" className="leading-9">책 유형 : </label>
            <div className="mx-auto w-9/12">
              <select
                  className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5"
                  defaultValue="default"
                  onChange={onChange}
                  name="bookType"
                  aria-label="옵션선택"
              >
                <option value="default" disabled style={{color: "#ccc"}}>
                  ---책 유형 선택---
                </option>
                {(optionValue || []).map((options, idx) => (
                    <option key={idx} value={options} style={{color: "#000"}}>
                      {options}
                    </option>
                ))}
              </select>
            </div>
          </div>
        </form>

        <div className="flex flex-low items-center justify-center ">
          <div>
            <button onClick={onclickBookAdd}
                    className="focus:outline-none text-white bg-yellow-400 hover:bg-yellow-500 focus:ring-4 focus:ring-yellow-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">
              책 등록
            </button>
          </div>

          <div>
            <Link to="/books">

              <button
                  className="focus:outline-none text-white bg-yellow-400 hover:bg-yellow-500 focus:ring-4 focus:ring-yellow-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">
                모든 책 조회
              </button>
            </Link>
          </div>

        </div>
      </div>

  );
}
