import {useState} from "react";
import axios from "axios";

export function BookRentalAdd() {

  const [rentalRequest, setRentalREquest] = useState({
    bookName: '',
    email: ''
  })

  const onChangeBookAdd = (e) => {
    const {name, value} = e.target;

    const nextBookInfo = {
      ...bookInfo,
      [name]: value
    }

    setBookInfo(nextBookInfo);
  }

  function validateBookInfo(bookInfo) {
    if (bookInfo.bookName === '' || bookInfo.bookType === '') {
      alert("입력값을 확인해주세요!");
      return false;
    }

    return true;
  }

  const onclickBookAdd = () => {
    if (validateBookInfo(bookInfo)) {
      axios
      .patch("http://localhost:8080/api/v1/books", JSON.stringify(bookInfo), {
        headers: {
          "Content-Type": `application/json`,
        },
      }).then(function (response) {
        console.log("responseStatus : " + response.status);
        console.log("console.data : " + response.data);
        console.log(response);
      })
      .catch(function (errorResponse) {
        console.log("status :" + errorResponse.response.status);
        console.log(errorResponse.response.data);
        alert(errorResponse.response.data.message);
      });

    }

  }

  return (
      <div className="fou_box content_box">
        <div className="content">
          <img src=""/>
          <p className="title">책 반납</p>
          <div className="input_box">
            <p>유저 이름</p>
            <input/>
          </div>
          <div className="input_box">
            <p>책이름</p>
            <input/>
          </div>
          <button className="save">저장</button>
        </div>
      </div>
  );
}
