import {User} from "./User";
import Header from "../../Header";
import {useEffect, useState} from "react";
import axios from "axios";

export function UserList() {


  const [users, setUsers] = useState([
  ]);

  // 리액트가 준비가 다 되면 호출되는 훅 함수
  useEffect(() => {

    axios.get("http://localhost:8080/api/v1/users")
    .then(v => setUsers(v.data))
    console.log(users)

  }, []);

  return (
      <div className="container mx-auto">
        <div>
          <Header headername="유저 목록"/>
        </div>
        <div>
          <table className="min-w-full border-collapse block md:table">
            <thead className="block md:table-header-group">
            <tr className="border border-grey-500 md:border-none block md:table-row absolute -top-full md:top-auto -left-full md:left-auto  md:relative ">
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">유저 Id</th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">유저 이메일
              </th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">유저 이름
              </th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">생성일</th>
              <th className="bg-gray-600 p-2 text-white font-bold md:border md:border-grey-500 text-left block md:table-cell">수정일</th>

            </tr>
            </thead>

            <tbody className="block md:table-row-group">
            {

              // eslint-disable-next-line array-callback-return
              users.map(user => {
                return (<tr className="bg-gray-300 border border-grey-500 md:border-none block md:table-row" key={user.userId}>
                      <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{user.email}</td>
                      <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{user.name}</td>
                      <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{user.createdAt}</td>
                      <td className="p-2 md:border md:border-grey-500 text-left block md:table-cell">{user.modifiedAt}</td>
                    </tr>
                )
              })
            }

            </tbody>
          </table>
        </div>
      </div>
  )
}
