import {useState} from "react";

export function BookAdd() {

  const book = useState({
    bookName: ""
  })

  const handleSubmit = (e) => {

    if (book.bookName === "") {
      alert("입력값을 확인해주세요!");
    } else {
      console.log(book.bookName)
    }
  }

  const optionValue = ["COMPUTER", "SCIENCE", "ECONOMY", "COMIC", "SPORTS"]

  return (
      <div className="m-8 ">
        <form>
          <div className="mr-4 flex flex-row">
            <label htmlFor="inputBookName"
                   className="">책 이름 : </label>
            <div className="mx-8">

              <input type="text" id="small-input"
                     className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500 focus:border-blue-500 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"/>

            </div>

          </div>

          <div className="my-8 flex flex-row">
            <label htmlFor="bookType"
                   className="">책 유형 : </label>
            <div className="mx-8">

              <select className="bg-gray-50 border border-gray-300 text-gray-900 text-sm rounded-lg focus:ring-blue-500 focus:border-blue-500 block w-full p-2.5 dark:bg-gray-700 dark:border-gray-600 dark:placeholder-gray-400 dark:text-white dark:focus:ring-blue-500 dark:focus:border-blue-500"
                  defaultValue="default"
                  // onChange={handleChange}
                  name="selectBox"
                  aria-label="옵션선택"
                  style={{
                    border: "1px solid #eee",
                    width: "100%",
                    height: "30px",
                    color: "#ccc",
                    marginBottom: "10px",
                    padding: "5px"
                  }}
              >
                <option value="default" disabled style={{color: "#ccc"}}>
                  ---책 유형 선택---
                </option>
                {(optionValue || []).map((options, idx) => (
                    <option key={idx} value={options} style={{color: "#000"}}>
                      {options}
                    </option>
                ))}
              </select>
            </div>

          </div>
        </form>

        <div className="flex items-center justify-center ">
          <button onClick={handleSubmit}
                  className="focus:outline-none text-white bg-yellow-400 hover:bg-yellow-500 focus:ring-4 focus:ring-yellow-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">
            책 등록
          </button>
        </div>
      </div>
);
}
