import {useState} from "react";
import {Link} from "react-router-dom";
import axios from "axios";

export function UserAdd() {

  const [user, setUser] = useState({
    email : '',
    name : ''
  });

  const onChangeUser = (e) => {
    const {name, value} = e.target;

    const userInfo = {
      ...user,
      [name]: value
    }

    setUser(userInfo);
  }

  function validateUser(user) {
    if (user.email === '' || user.name === '') {
      alert("입력값을 확인해주세요!");
      return false;
    }

    return true;
  }

  const onclickBookAdd = () => {
    if (validateUser(user)) {
      axios
      .post("http://localhost:8080/api/v1/users", JSON.stringify(user), {
        headers: {
          "Content-Type": `application/json`,
        },
      }).then(function (response) {
        console.log("responseStatus : " + response.status);
        console.log("console.data : " + response.data);
        console.log(response);
      })
      .catch(function (errorResponse) {
        console.log("status :" + errorResponse.response.status);
        console.log(errorResponse.response.data);
        alert(errorResponse.response.data.message);
      });

    }

  }

  return (
      <div className="m-4 ">

        <form>
          <div className="flex flex-row py-4">
            <div className="w-24">
              <label htmlFor="inputBookName"
                     className="leading-9">유저 이름 : </label>
            </div>
            <div className="mx-auto w-9/12">
              <input type="text" id="small-input" onChange={onChangeUser}
                     className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500"/>
            </div>
          </div>

          <div className=" flex flex-row">
            <div className="w-24">
              <label htmlFor="inputBookName"
                     className="leading-9"> 이메일 : </label>
            </div>
            <div className="mx-auto w-9/12">
              <input type="email" id="small-input" onChange={onChangeUser}
                     className="block w-full p-2 text-gray-900 border border-gray-300 rounded-lg bg-gray-50 sm:text-xs focus:ring-blue-500"/>
            </div>
          </div>

        </form>

        <div className="flex flex-low items-center justify-center p-8 ">
          <div>
            <button onClick={handleSubmit}
                    className="focus:outline-none text-white bg-yellow-400 hover:bg-yellow-500 focus:ring-4 focus:ring-yellow-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">
              유저 등록
            </button>
          </div>

          <div>
            <Link to="users">
              <button
                      className="focus:outline-none text-white bg-yellow-400 hover:bg-yellow-500 focus:ring-4 focus:ring-yellow-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">
                모든 유저 조회
              </button>
            </Link>
          </div>

        </div>
      </div>

  );
}
