import {useLocation} from "react-router-dom";
import Header from "../../Header";
import {useEffect, useState} from "react";
import axios from "axios";

export function UserRentalList() {

  const location = useLocation();

  const userId = location.state.userId;

  const [userRentals, setUserRentals] = useState([
    {
      userId: '',
      email: '',
      name: '',
      books: [],
    }
  ]);

  useEffect(() => {
    axios.get("http://localhost:8080/api/v1/users/"+ userId + "/rentals")
    .then(function (success) {
      console.log(success.data);
      setUserRentals(success.data)
    });
  }, []);

  return (
      <div>
        <div>
          <Header headername="유저 대여 목록"/>
        </div>

        <div>

        </div>

        <div>
          <table className="table_container">
            <thead className="thead">
            <tr className="list_wrap">
              <th className="list_name">책 Id</th>
              <th className="list_name">책 이름</th>
              <th className="list_name">책 유형</th>
              <th className="list_name">책 대여 상태</th>
              <th className="list_name">책 등록일</th>
              <th className="list_name">대여하기</th>

            </tr>
            </thead>

            <tbody>
            {

              // eslint-disable-next-line array-callback-return
              userRentals.books && userRentals.books.map(book => {
                return (<tr className="tr" key={book.id}>
                      <td className="">{book.id}</td>
                      <td className="">{book.bookName}</td>
                      <td className="">{book.bookType}</td>
                      <td className="">{book.bookStatus}</td>
                      <td className="">{book.createdAt}</td>
                      <td>
                        {
                          book.bookStatus === 'RENTED'
                              ?  <button className="booking_no_btn">대여 불가
                              </button>
                              : <button className="booking_btn">대여 하기</button>
                        }

                      </td>

                    </tr>
                )
              })
            }



            </tbody>
          </table>

        </div>

        {location.state.userId}
      </div>
  )
}
